sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if ( newValue == '') {
      return;
    }

    var tpt = g_form.getValue('training_program_type');

    // Clear all of the choices from the Training type field choice list
    g_form.clearOptions('training_program_type');

    // If the value of the Training program category field is hr, add
    // two hr choices and other to the 'training_program_type' field choice list
    if(newValue == 1 ){
      g_form.addOption('training_program_type','1_1','HR1');
      g_form.addOption('training_program_type','1_2','HR2');
      g_form.addOption('training_program_type','_111','Other');
    }
    // If the value of the Training program category  field is Managerial, add
    // three Managerial choices and other to the 'training_program_type' field
    // choice list
    if(newValue == 2 ){
      g_form.addOption('training_program_type','2_1','Project Management');
      g_form.addOption('training_program_type','2_2','Operation Management');
      g_form.addOption('training_program_type','2_3','Product Management');
      g_form.addOption('training_program_type','_111','Other');
    }
    // If the value of the Training program category  field is Technical, add
    // two Technical choices and other to the 'training_program_type' field
    // choice list
    if(newValue == 3 ){
      g_form.addOption('training_program_type','3_2','Programming languages');
      g_form.addOption('training_program_type','3_3','Statistical tools');
      g_form.addOption('training_program_type','_111','Other');
    }

	if(newValue == 111) {
	g_form.addOption('training_program_type','_111','Other');
	}


    // If the form is loading and it is not a new record, set the u_what_needed value to the
    // value from the record before it was loaded
    if(isLoading && !g_form.isNewRecord()){
      g_form.setValue('training_program_type', tpt);
    }
  }
